const { Pool } = require('pg');
require('dotenv').config();

// Database configuration
const dbConfig = {
  connectionString: process.env.DATABASE_URL,
  ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false,
  max: 20, // Maximum number of connections
  idleTimeoutMillis: 30000, // How long a client is allowed to remain idle
  connectionTimeoutMillis: 10000, // Return an error after failing to connect for this long
  statement_timeout: 30000, // Statement timeout in milliseconds
  query_timeout: 30000, // Query timeout in milliseconds
};

// Create connection pool
const pool = new Pool(dbConfig);

// Test connection on startup
pool.on('connect', () => {
  console.log('‚úÖ Nova conex√£o estabelecida com PostgreSQL');
});

pool.on('error', (err) => {
  console.error('‚ùå Erro inesperado no cliente PostgreSQL:', err);
  process.exit(-1);
});

// Helper function to execute queries
const query = async (text, params) => {
  const start = Date.now();
  try {
    const result = await pool.query(text, params);
    const duration = Date.now() - start;
    console.log('üìä Query executada:', { text, duration, rows: result.rowCount });
    return result;
  } catch (error) {
    console.error('‚ùå Erro na query:', { text, error: error.message });
    throw error;
  }
};

// Helper function to get a client from the pool
const getClient = async () => {
  return await pool.connect();
};

// Transaction helper
const transaction = async (callback) => {
  const client = await pool.connect();
  try {
    await client.query('BEGIN');
    const result = await callback(client);
    await client.query('COMMIT');
    return result;
  } catch (error) {
    await client.query('ROLLBACK');
    throw error;
  } finally {
    client.release();
  }
};

// Graceful shutdown
process.on('SIGINT', async () => {
  console.log('üîí Fechando pool de conex√µes...');
  await pool.end();
  console.log('‚úÖ Pool de conex√µes fechado');
  process.exit(0);
});

module.exports = {
  pool,
  query,
  getClient,
  transaction
};

