const jwt = require('jsonwebtoken');
const { query } = require('../database/connection');

const authenticateToken = async (req, res, next) => {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1]; // Bearer TOKEN

  if (!token) {
    return res.status(401).json({ 
      error: 'Token de acesso requerido',
      code: 'NO_TOKEN'
    });
  }

  try {
    // Verifica o token JWT
    const decoded = jwt.verify(token, process.env.JWT_SECRET || 'rh-pro-secret-key-2025');

    // Busca o usu√°rio no banco
    const result = await query(
      'SELECT id, email, name, role, is_active FROM users WHERE id = $1',
      [decoded.userId]
    );

    if (result.rows.length === 0) {
      return res.status(401).json({ 
        error: 'Usu√°rio n√£o encontrado',
        code: 'USER_NOT_FOUND'
      });
    }

    const user = result.rows[0];

    if (!user.is_active) {
      return res.status(401).json({ 
        error: 'Usu√°rio inativo',
        code: 'USER_INACTIVE'
      });
    }

    // Adiciona o usu√°rio ao request
    req.user = user;
    next();

  } catch (error) {
    console.error('Erro na autentica√ß√£o:', error);

    if (error.name === 'TokenExpiredError') {
      return res.status(401).json({ 
        error: 'Token expirado',
        code: 'TOKEN_EXPIRED'
      });
    }

    if (error.name === 'JsonWebTokenError') {
      return res.status(401).json({ 
        error: 'Token inv√°lido',
        code: 'INVALID_TOKEN'
      });
    }

    return res.status(500).json({ 
      error: 'Erro interno do servidor',
      code: 'INTERNAL_ERROR'
    });
  }
};

// Middleware para verificar permiss√µes espec√≠ficas
const requireRole = (roles) => {
  return (req, res, next) => {
    if (!req.user) {
      return res.status(401).json({ 
        error: 'Usu√°rio n√£o autenticado',
        code: 'NOT_AUTHENTICATED'
      });
    }

    const userRole = req.user.role;
    const allowedRoles = Array.isArray(roles) ? roles : [roles];

    if (!allowedRoles.includes(userRole)) {
      return res.status(403).json({ 
        error: 'Permiss√£o insuficiente',
        code: 'INSUFFICIENT_PERMISSION',
        required: allowedRoles,
        current: userRole
      });
    }

    next();
  };
};

// Middleware para log de auditoria
const auditLog = (action) => {
  return async (req, res, next) => {
    // Log da a√ß√£o ser√° implementado depois da resposta
    const originalSend = res.send;

    res.send = function(data) {
      // Log da auditoria de forma ass√≠ncrona
      setImmediate(async () => {
        try {
          await query(
            `INSERT INTO audit_logs (user_id, action, ip_address, user_agent, new_values)
             VALUES ($1, $2, $3, $4, $5)`,
            [
              req.user?.id,
              action,
              req.ip,
              req.get('User-Agent'),
              JSON.stringify({ 
                method: req.method,
                path: req.path,
                query: req.query,
                body: req.method !== 'GET' ? req.body : undefined
              })
            ]
          );
        } catch (error) {
          console.error('Erro no log de auditoria:', error);
        }
      });

      originalSend.call(this, data);
    };

    next();
  };
};

module.exports = {
  authenticateToken,
  requireRole,
  auditLog
};

