const express = require('express');
const bcrypt = require('bcryptjs');
const jwt = require('jsonwebtoken');
const { body, validationResult } = require('express-validator');
const { query } = require('../database/connection');
const router = express.Router();

const JWT_SECRET = process.env.JWT_SECRET || 'rh-pro-secret-key-2025';
const JWT_EXPIRES_IN = process.env.JWT_EXPIRES_IN || '24h';

// POST /api/auth/login
router.post('/login', [
  body('email').isEmail().normalizeEmail().withMessage('Email inv√°lido'),
  body('password').isLength({ min: 6 }).withMessage('Senha deve ter no m√≠nimo 6 caracteres')
], async (req, res) => {
  try {
    // Verifica erros de valida√ß√£o
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.status(400).json({
        error: 'Dados inv√°lidos',
        details: errors.array()
      });
    }

    const { email, password } = req.body;

    // Busca usu√°rio no banco
    const result = await query(
      'SELECT id, email, password_hash, name, role, is_active FROM users WHERE email = $1',
      [email]
    );

    if (result.rows.length === 0) {
      return res.status(401).json({
        error: 'Credenciais inv√°lidas'
      });
    }

    const user = result.rows[0];

    // Verifica se usu√°rio est√° ativo
    if (!user.is_active) {
      return res.status(401).json({
        error: 'Usu√°rio inativo'
      });
    }

    // Verifica senha
    const isValidPassword = await bcrypt.compare(password, user.password_hash);
    if (!isValidPassword) {
      return res.status(401).json({
        error: 'Credenciais inv√°lidas'
      });
    }

    // Atualiza √∫ltimo login
    await query(
      'UPDATE users SET last_login = CURRENT_TIMESTAMP WHERE id = $1',
      [user.id]
    );

    // Gera token JWT
    const token = jwt.sign(
      { 
        userId: user.id, 
        email: user.email, 
        role: user.role 
      },
      JWT_SECRET,
      { expiresIn: JWT_EXPIRES_IN }
    );

    res.json({
      success: true,
      message: 'Login realizado com sucesso',
      token,
      user: {
        id: user.id,
        email: user.email,
        name: user.name,
        role: user.role
      }
    });

  } catch (error) {
    console.error('Erro no login:', error);
    res.status(500).json({
      error: 'Erro interno do servidor'
    });
  }
});

// POST /api/auth/register
router.post('/register', [
  body('email').isEmail().normalizeEmail().withMessage('Email inv√°lido'),
  body('password').isLength({ min: 6 }).withMessage('Senha deve ter no m√≠nimo 6 caracteres'),
  body('name').isLength({ min: 2 }).withMessage('Nome deve ter no m√≠nimo 2 caracteres'),
  body('role').optional().isIn(['admin', 'hr', 'manager']).withMessage('Papel inv√°lido')
], async (req, res) => {
  try {
    const errors = validationResult(req);
    if (!errors.isEmpty()) {
      return res.status(400).json({
        error: 'Dados inv√°lidos',
        details: errors.array()
      });
    }

    const { email, password, name, role = 'hr' } = req.body;

    // Verifica se usu√°rio j√° existe
    const existingUser = await query(
      'SELECT id FROM users WHERE email = $1',
      [email]
    );

    if (existingUser.rows.length > 0) {
      return res.status(409).json({
        error: 'Email j√° cadastrado'
      });
    }

    // Criptografa senha
    const passwordHash = await bcrypt.hash(password, 12);

    // Insere novo usu√°rio
    const result = await query(
      'INSERT INTO users (email, password_hash, name, role) VALUES ($1, $2, $3, $4) RETURNING id, email, name, role',
      [email, passwordHash, name, role]
    );

    const newUser = result.rows[0];

    res.status(201).json({
      success: true,
      message: 'Usu√°rio criado com sucesso',
      user: newUser
    });

  } catch (error) {
    console.error('Erro no registro:', error);
    res.status(500).json({
      error: 'Erro interno do servidor'
    });
  }
});

// POST /api/auth/refresh
router.post('/refresh', async (req, res) => {
  try {
    const { token } = req.body;

    if (!token) {
      return res.status(401).json({
        error: 'Token requerido'
      });
    }

    // Verifica token (mesmo que expirado)
    let decoded;
    try {
      decoded = jwt.verify(token, JWT_SECRET);
    } catch (error) {
      if (error.name === 'TokenExpiredError') {
        // Permite refresh de tokens expirados at√© 7 dias
        decoded = jwt.decode(token);
        const expiredDate = new Date(decoded.exp * 1000);
        const now = new Date();
        const daysDiff = (now - expiredDate) / (1000 * 60 * 60 * 24);

        if (daysDiff > 7) {
          return res.status(401).json({
            error: 'Token expirado h√° muito tempo'
          });
        }
      } else {
        return res.status(401).json({
          error: 'Token inv√°lido'
        });
      }
    }

    // Busca usu√°rio atualizado
    const result = await query(
      'SELECT id, email, name, role, is_active FROM users WHERE id = $1',
      [decoded.userId]
    );

    if (result.rows.length === 0 || !result.rows[0].is_active) {
      return res.status(401).json({
        error: 'Usu√°rio n√£o encontrado ou inativo'
      });
    }

    const user = result.rows[0];

    // Gera novo token
    const newToken = jwt.sign(
      { 
        userId: user.id, 
        email: user.email, 
        role: user.role 
      },
      JWT_SECRET,
      { expiresIn: JWT_EXPIRES_IN }
    );

    res.json({
      success: true,
      token: newToken,
      user: {
        id: user.id,
        email: user.email,
        name: user.name,
        role: user.role
      }
    });

  } catch (error) {
    console.error('Erro no refresh:', error);
    res.status(500).json({
      error: 'Erro interno do servidor'
    });
  }
});

// GET /api/auth/me - Retorna dados do usu√°rio logado
router.get('/me', async (req, res) => {
  try {
    const authHeader = req.headers['authorization'];
    const token = authHeader && authHeader.split(' ')[1];

    if (!token) {
      return res.status(401).json({
        error: 'Token requerido'
      });
    }

    const decoded = jwt.verify(token, JWT_SECRET);

    const result = await query(
      'SELECT id, email, name, role, is_active, last_login, created_at FROM users WHERE id = $1',
      [decoded.userId]
    );

    if (result.rows.length === 0) {
      return res.status(404).json({
        error: 'Usu√°rio n√£o encontrado'
      });
    }

    const user = result.rows[0];

    if (!user.is_active) {
      return res.status(401).json({
        error: 'Usu√°rio inativo'
      });
    }

    res.json({
      success: true,
      user: {
        id: user.id,
        email: user.email,
        name: user.name,
        role: user.role,
        lastLogin: user.last_login,
        createdAt: user.created_at
      }
    });

  } catch (error) {
    console.error('Erro ao buscar usu√°rio:', error);

    if (error.name === 'JsonWebTokenError') {
      return res.status(401).json({
        error: 'Token inv√°lido'
      });
    }

    if (error.name === 'TokenExpiredError') {
      return res.status(401).json({
        error: 'Token expirado'
      });
    }

    res.status(500).json({
      error: 'Erro interno do servidor'
    });
  }
});

module.exports = router;

