const express = require('express');
const { body, validationResult, query: expressQuery } = require('express-validator');
const { query } = require('../database/connection');
const { auditLog } = require('../middleware/auth');
const router = express.Router();

// GET /api/employees - Lista todos os funcion√°rios
router.get('/', async (req, res) => {
  try {
    const { page = 1, limit = 50, search = '', department = '', active = 'true' } = req.query;
    const offset = (page - 1) * limit;

    let whereConditions = [];
    let params = [];
    let paramCount = 0;

    // Filtro de busca
    if (search) {
      paramCount++;
      whereConditions.push(`(name ILIKE $${paramCount} OR email ILIKE $${paramCount} OR employee_code ILIKE $${paramCount})`);
      params.push(`%${search}%`);
    }

    // Filtro de departamento
    if (department) {
      paramCount++;
      whereConditions.push(`department_id = $${paramCount}`);
      params.push(department);
    }

    // Filtro de ativo/inativo
    if (active !== 'all') {
      paramCount++;
      whereConditions.push(`is_active = $${paramCount}`);
      params.push(active === 'true');
    }

    const whereClause = whereConditions.length > 0 ? `WHERE ${whereConditions.join(' AND ')}` : '';

    // Query principal
    const employeesQuery = `
      SELECT 
        e.*,
        d.name as department_name
      FROM employees e
      LEFT JOIN departments d ON e.department_id = d.id
      ${whereClause}
      ORDER BY e.name ASC
      LIMIT $${paramCount + 1} OFFSET $${paramCount + 2}
    `;

    params.push(limit, offset);

    // Query de contagem total
    const countQuery = `
      SELECT COUNT(*) 
      FROM employees e 
      LEFT JOIN departments d ON e.department_id = d.id 
      ${whereClause}
    `;

    const [employeesResult, countResult] = await Promise.all([
      query(employeesQuery, params),
      query(countQuery, params.slice(0, -2)) // Remove limit e offset
    ]);

    const total = parseInt(countResult.rows[0].count);
    const totalPages = Math.ceil(total / limit);

    res.json({
      success: true,
      data: employeesResult.rows,
      pagination: {
        page: parseInt(page),
        limit: parseInt(limit),
        total,
        totalPages,
        hasNext: page < totalPages,
        hasPrev: page > 1
      }
    });

  } catch (error) {
    console.error('Erro ao buscar funcion√°rios:', error);
    res.status(500).json({
      error: 'Erro interno do servidor'
    });
  }
});

// GET /api/employees/:id - Busca funcion√°rio por ID
router.get('/:id', async (req, res) => {
  try {
    const { id } = req.params;

    const result = await query(`
      SELECT 
        e.*,
        d.name as department_name
      FROM employees e
      LEFT JOIN departments d ON e.department_id = d.id
      WHERE e.id = $1
    `, [id]);

    if (result.rows.length === 0) {
      return res.status(404).json({
        error: 'Funcion√°rio n√£o encontrado'
      });
    }

    res.json({
      success: true,
      data: result.rows[0]
    });

  } catch (error) {
    console.error('Erro ao buscar funcion√°rio:', error);
    res.status(500).json({
      error: 'Erro interno do servidor'
    });
  }
});

// POST /api/employees - Cria novo funcion√°rio
router.post('/', [
  body('name').isLength({ min: 2 }).withMessage('Nome deve ter no m√≠nimo 2 caracteres'),
  body('email').optional().isEmail().normalizeEmail().withMessage('Email inv√°lido'),
  body('employee_code').isLength({ min: 1 }).withMessage('C√≥digo do funcion√°rio √© obrigat√≥rio'),
  body('cpf').optional().isLength({ min: 11, max: 14 }).withMessage('CPF inv√°lido'),
  body('hire_date').isISO8601().withMessage('Data de admiss√£o inv√°lida'),
  body('department_id').optional().isUUID().withMessage('ID do departamento inv√°lido'),
