    if (user_id) {
      paramCount++;
      whereConditions.push(`a.user_id = $${paramCount}`);
      params.push(user_id);
    }

    if (action) {
      paramCount++;
      whereConditions.push(`a.action ILIKE $${paramCount}`);
      params.push(`%${action}%`);
