      FROM audit_logs a
      ${whereClause}
    `, params);

    res.json({
      success: true,
      data: {
        logs: auditLogs.rows,
        statistics: auditStats.rows[0],
        period: {
          start_date: start_date || 'in√≠cio',
          end_date: end_date || 'hoje'
        }
      }
