export default function Employees() {
  const [employees, setEmployees] = useState([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState('')
  const [filteredEmployees, setFilteredEmployees] = useState([])

  useEffect(() => {
    fetchEmployees()
  }, [])

  useEffect(() => {
    // Filtrar funcion√°rios baseado no termo de busca
    const filtered = employees.filter(employee =>
      employee.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      employee.email?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      employee.employee_code?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      employee.department_name?.toLowerCase().includes(searchTerm.toLowerCase())
    )
    setFilteredEmployees(filtered)
  }, [employees, searchTerm])

  const fetchEmployees = async () => {
    try {
      setLoading(true)
      const response = await axios.get('/api/employees')
      setEmployees(response.data.data || [])
    } catch (error) {
      console.error('Erro ao buscar funcion√°rios:', error)
      toast.error('Erro ao carregar funcion√°rios')
    } finally {
      setLoading(false)
    }
  }

  const handleEdit = (employee) => {
    // Implementar modal de edi√ß√£o ou navega√ß√£o para p√°gina de edi√ß√£o
    toast.info(`Editar funcion√°rio: ${employee.name}`)
  }

  const handleDelete = async (employee) => {
    if (window.confirm(`Tem certeza que deseja inativar o funcion√°rio ${employee.name}?`)) {
      try {
        await axios.delete(`/api/employees/${employee.id}`)
        toast.success('Funcion√°rio inativado com sucesso')
        fetchEmployees() // Recarregar lista
      } catch (error) {
        console.error('Erro ao inativar funcion√°rio:', error)
        toast.error('Erro ao inativar funcion√°rio')
      }
    }
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-thermas-600"></div>
      </div>
    )
  }

  return (
    <div>
      <div className="sm:flex sm:items-center">
        <div className="sm:flex-auto">
          <h1 className="text-2xl font-semibold text-gray-900">Funcion√°rios</h1>
          <p className="mt-2 text-sm text-gray-700">
            Lista de todos os funcion√°rios da empresa ({employees.length} funcion√°rios).
          </p>
        </div>
        <div className="mt-4 sm:mt-0 sm:ml-16 sm:flex-none">
          <button
            type="button"
            className="inline-flex items-center justify-center rounded-md border border-transparent bg-thermas-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-thermas-700 focus:outline-none focus:ring-2 focus:ring-thermas-500 focus:ring-offset-2 sm:w-auto"
            onClick={() => toast.info('Funcionalidade de adicionar funcion√°rio em desenvolvimento')}
          >
            <PlusIcon className="h-4 w-4 mr-2" />
            Adicionar funcion√°rio
          </button>
        </div>
      </div>

      {/* Search Bar */}
      <div className="mt-6">
        <div className="relative">
          <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
            <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" />
          </div>
          <input
            type="text"
            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:placeholder-gray-400 focus:ring-1 focus:ring-thermas-500 focus:border-thermas-500 sm:text-sm"
            placeholder="Buscar por nome, email, c√≥digo ou departamento..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      {/* Employee Grid */}
      <div className="mt-6">
        {filteredEmployees.length === 0 ? (
          <div className="text-center py-12">
